mapper.insert:
INSERT INTO db_bbsattach (
	attachNo
	, attachBbsNo
	, attachOriginalFile
	, attachSavedFile
) VALUES (
	db_bbs_attach_seq.NEXTVAL
	, #{attachBbsNo}
	, #{attachOriginalFile}
	, #{attachSavedFile}
)

mapper.update:
UPDATE 
	db_bbs_attach 
SET
	attachNo = db_bbs_attach_seq.NEXTVAL
	, attachOriginalFile = #{attachOriginalFile}
	, attachSavedFile = #{attachSavedFile}
WHERE
	attachBbsNo = #{attachBbsNo}
