mapper.insert:
INSERT INTO db_bbs_attach (
	attachNo
	, attachBbsNo
	, attachOriginalFile
	, attachSavedFile
) VALUES (
	db_bbs_attach_seq.NEXTVAL
	, #{attachBbsNo}
	, #{attachOriginalFile}
	, #{attachSavedFile}
)

mapper.update:
UPDATE 
	db_bbs_attach 
SET
	attachNo = db_bbs_attach_seq.NEXTVAL
	, attachOriginalFile = #{attachOriginalFile}
	, attachSavedFile = #{attachSavedFile}
WHERE
	attachBbsNo = #{attachBbsNo}

mapper.delete:
DELETE FROM db_like
WHERE
	likeUserNo = #{likeUserNo}
AND
	likeBbsNo = #{likeBbsNo}

mapper.update:
UPDATE db_reply
SET	
	replyContent = #{replyContent}
	, replyEditDate = SYSDATE
WHERE
	replyNo = #{replyNo}
